$(function(){$(".photos .check-all, .photos .uncheck-all").click(function(c){c.preventDefault();var e=$(this).closest("form").find("input[type=checkbox]");$(this).hasClass("check-all")?e.prop("checked",!0):$(this).hasClass("uncheck-all")&&e.prop("checked",!1)})});